con:configuration.scriptText:
//validate the response time
assert messageExchange.timeTaken <1000

//validate the endpoint
def endpoint = "http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
assert messageExchange.getEndpoint() == endpoint
log.info messageExchange.getEndpoint()

//validate the time taken
log.info messageExchange.getTimeTaken()

//validate the response header
log.info messageExchange.responseHeaders["Content-Type"]
assert messageExchange.responseHeaders["Content-Type"]!=null

//validate attachments in the respone
assert messageExchange.responseAttachments.length ==0

//validate response nodes/messages
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context) //groovy utils is a class in soapui in groovy
def requestHolder = groovyUtils.getXmlHolder (messageExchange.requestContent)//with the groovy object get the request content
def responseHolder = groovyUtils.getXmlHolder (messageExchange.responseContent)
def refNum  = responseHolder.getNodeValue ("//m:CountryCurrencyResult/m:sName")
log.info refNum
assert refNum =="Rupees"

//validate json nodes and values import groovy.json.JsonSlurper def responsemessage = messageExchange.response.responseContent def json = new JsonSlurper().parseText(responsemessage)
assert json.capital.toString()!=null assert json.name.toString() == "[British Indian Ocean Territory, India]"
xmlHold = messageExchange.responseContentAsXml.toString()
